package operator;

public class urnaryOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=20;
		//i++;
		System.out.println(i++); // it will print 20 coz first value is printed and then increment i.e post increment 
		System.out.println(i);// it will print 21
		System.out.println(++i);// it increment value first and then print i.e pre increment
		i--;
		
		System.out.println(i--); // it will print 21 coz as it is value will print then decrement by 1
		System.out.println(i);// 20
		
		--i;
		System.out.println(--i); // it will print 18 
		System.out.println(i); //18
		
		
		

	}

}
